import * as WebBrowser from 'expo-web-browser';
import React from 'react';
import { StyleSheet, TouchableOpacity } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';

import Colors from '../constants/Colors';
import { MonoText } from './StyledText';
import { Text, View } from './Themed';

const DATA = [
  {
    id: '01',
    title: 'Rework this "EditScreenInfo.tsx" page to be a simple welcome screen.'
  },
  { 
    id: '02',
    title: 'The page must contain a nice icon that is appropriate.'
  },
  { 
    id: '03',
    title: 'The page must contain some friendly welcoming text.'
  }
]


export default function EditScreenInfo({ path }: { path: string }) {

  type DataProps = {
    title: string;
  }

  type ItemProps = {
    item: any;
  }

  const ListItem = ({title}: DataProps) => (
    <View>
      <Text style={styles.bulletList}>{title}</Text>
    </View>
  );
  const renderItem = ({item}: ItemProps) => (
    <ListItem title={item.title} />
  );
  return (
    <View>
      <View style={styles.getStartedContainer}>
        <Text
          style={styles.getStartedText}
          lightColor="rgba(0,0,0,0.8)"
          darkColor="rgba(255,255,255,0.8)">
          Instructions:
        </Text>

        <Text
          style={styles.instructionsText}
          lightColor="rgba(0,0,0,0.8)"
          darkColor="rgba(255,255,255,0.8)">
          <FlatList
            data={DATA}
            renderItem={renderItem}
            keyExtractor={item => item.id}
          />
        </Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  getStartedContainer: {
    marginHorizontal: 50,
  },
  homeScreenFilename: {
    marginVertical: 7,
  },
  getStartedText: {
    fontSize: 24,
    lineHeight: 32,
    textAlign: 'left',
    marginBottom: 25
  },
  instructionsText: {
    fontSize: 16,
    lineHeight: 20,
    textAlign: 'left',
  },
  bulletList: {
    marginTop: 0,
    marginBottom: 10,
    width: '100%',
  },
  helpContainer: {
    marginTop: 15,
    marginHorizontal: 20,
    alignItems: 'center',
  },
  helpLink: {
    paddingVertical: 15,
  },
  helpLinkText: {
    textAlign: 'center',
  },
});
